#! perl

use strict;
use warnings;

my $input = "



";

#    1
#  2 3 4
#5 6 7 8 9
#  A B C
#    D


#             0,2
#      -1,1   0,1   1,1
#-2,0  -1,0   0,0   1,0  2,0
#      -1,-1  0,-1  1,-1
#             0,-2


my @instructions = split /\n/, $input;

my $x = -2;
my $y = 0;


my @code;

foreach my $instruction(@instructions){
  for my $c (split //, $instruction) {

    my $tempx = $x;
    my $tempy = $y;

    if($c eq 'U') { $tempy++; }
    elsif($c eq 'D') { $tempy--}
    elsif($c eq 'L') { $tempx--; }
    elsif($c eq 'R') { $tempx++; }
    else{die;}

    isValid($tempx, $tempy);

  }

  push @code, "$x,$y";

}

print join ("\n", @code);


sub isValid{
  my ($tempx, $tempy) = @_;
  if( abs($tempx) < 2 && abs($tempy) < 2 || abs($tempx) == 2 && $tempy == 0 || abs($tempx) == 2 && $tempy == 0){
    $x = $tempx;
    $y = $tempy;
  }
}

